import { getSuggestionListApi } from '@/api/search';
export default {
  props: {
    // 接收父组件的输入的值
    inputVal: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      // 联想建议的数组
      suggestions: []
    };
  },
  watch: {
    inputVal: {
      handler(oldVal, newVal) {
        console.log(newVal);
        this.onLoad(newVal);
      }
    }
  },
  methods: {
    async onLoad(val) {
      // console.log(val)
      // 如果搜索有值，发送请求
      if (val) {
        try {
          const {
            data: res
          } = await getSuggestionListApi(val);
          // console.log(res.data)
          this.suggestions = res.data.options;
          console.log(this.suggestions);
        } catch (error) {
          console.log(error);
        }
      } else {
        this.suggestions = [];
      }
    }
  }
};
