import { goToCommentLike, delCommentLike } from '@/api/comment';
export default {
  props: {
    commentId: {
      type: [Number, String, Object],
      required: true
    },
    isFollowed: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      followLoading: false,
      //按钮loading状态，默认关闭
      likeCount: 0
    };
  },
  model: {
    prop: 'isFollowed',
    event: 'changeStatus'
  },
  methods: {
    async onLike() {
      // 开启按钮loading状态
      this.followLoading = true;
      try {
        if (!this.isFollowed) {
          // 如果没有点赞
          const {
            data
          } = await goToCommentLike(this.commentId);
          console.log('comLike', data);
          this.$toast('点赞成功');
          this.likeCount++;
        } else {
          const {
            data
          } = await delCommentLike(this.commentId);
          this.$toast('取消点赞');
          this.likeCount--;
        }
        this.$emit('changeStatus', !this.isFollowed);
      } catch (error) {
        console.log(error);
      }
      this.followLoading = false;
    }
  }
};
