import { getSuggestionListApi } from '@/api/search';
// 引入防抖函数
import { debounce } from 'lodash';
export default {
  props: {
    // 接收父组件的输入的值
    inputVal: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      // 联想建议的数组
      suggestions: []
    };
  },
  watch: {
    inputVal: {
      // handler(newVal, oldVal) {
      //   // console.log('newVal', newVal)
      //   // console.log('oldVal', oldVal)
      //   this.loadSuggestions(newVal)
      // },
      handler: debounce(function (newVal) {
        this.loadSuggestions(newVal);
      }, 1000),
      immediate: true //侦听开始之后立调用
    }
  },

  methods: {
    async loadSuggestions(val) {
      // console.log(val)
      // 如果搜索有值，发送请求
      if (val) {
        try {
          const {
            data: res
          } = await getSuggestionListApi(val);
          // console.log(res.data)
          this.suggestions = res.data.options[0] ? res.data.options : [];
          console.log(this.suggestions);
        } catch (error) {
          this.$toast('数据获取失败，请稍后重试');
        }
      } else {
        this.suggestions = [];
      }
    }
  }
};
