import request from '@/utils/request'
// 请求 获取评论列表数据
export const getCommentListApi = (params) => {
  // console.log(params)
  return request({
    url: '/v1_0/comments',
    method: 'GET',
    params,
  })
}

// 请求 对文章或者评论进行评论
export const replyApi = (target) => {
  // console.log(params)
  return request({
    url: ' /v1_0/comments',
    method: 'POST',
    data: {
      target,
    },
  })
}
